mapper.update:
update recipe
set name = #{recipeName}, serveamount = #{serveAmount}, preparationTime = #{preparationTime}, cookingTime = #{cookingTime}
where recipe_id = #{recipeId}

mapper.select:
select recipe_id as recipeId, name as recipeName, serveamount as serveAmount, preparationTime, cookingTime
from recipe
where recipe_id = #{recipeId}

select recipe_id as recipeId, name as recipeName, serveamount as serveAmount, preparationTime, cookingTime
from recipe
where name = #{recipeName}

select recipe_id as recipeId, name as recipeName, serveamount as serveAmount, preparationTime, cookingTime
from recipe
where category = #{category}
