mapper.update:
update recipe
set name = #{recipeName}, serveamount = #{serveAmount}, preparationTime = #{preparationTime}, cookingTime = #{cookingTime}, image_url = #{imageUrl}
where recipe_id = #{recipeId}

mapper.select:
select recipe_id as recipeId, name as recipeName, serveamount as serveAmount, preparationTime, cookingTime,image_url as imageUrl
from recipe
where recipe_id = #{recipeId}

select recipe_id as recipeId, name as recipeName, serveamount as serveAmount, preparationTime, cookingTime,image_url as imageUrl
from recipe
where category = #{category}
